mapper.select:
select ID, CODE, NAME, IS_WORK_SHOP_MODEL, MODEL_TYPE, IS_DEL, STATUS, ENABLE, CREATE_USER, DATE_FORMAT(CREATE_TIME, '%Y-%m-%d %T') CREATE_TIME, LAST_UPDATE_USER, DATE_FORMAT(LAST_UPDATE_TIME, '%Y-%m-%d %T') LAST_UPDATE_TIME, (select NAME from sys_user where USERNAME=k.LAST_UPDATE_USER) LAST_UPDATE_EMP_NAME, PREPARATION1, PREPARATION2, PREPARATION3, PREPARATION4, PREPARATION5, PREPARATION6 from BD_KPI_MODEL k where IS_DEL = 0
and NAME like concat('%', #{kpiName}, '%')
and IS_WORK_SHOP_MODEL=1 and IS_WORK_SHOP_MODEL=2 and 1=1 and MODEL_TYPE=#{searchField}
ORDER BY ${sortKey} ${sortMethod}
ORDER BY ID desc

select COUNT(ID) COUNT
from bd_position_level
where ISDEL=0 and ATTACH_KPI_MODEL=#{MODEL_ID}
