mapper.select:
select * from SYS_USER 
where 1=1

and USERNAME = #{USERNAME} and PASSWORD=#{PASSWORD}


and USER_ID = #{USER_ID}

mapper.update:
update SYS_USER set 
LAST_LOGIN=#{LAST_LOGIN} 
where USER_ID=#{USER_ID}

mapper.select:
select u.USERNAME
from SYS_USER u
where u.STATUS=0 and u.NUMBER=#{number}

select 
	u.*,(select JOB_RANK 
	from bd_position_level where ID=(
		select EMP_GRADE_ID from bd_employee where EMP_CODE=u.NUMBER )
	) JOB_RANK
from
	sys_user u
where
	u.OPEN_ID = #{openId}

select ONLINE_TIME_CHANGE
FROM  sys_user
where USER_ID=#{USER_ID}

mapper.update:
update sys_user set 
TOTAL_TIME=#{ONLINE_TIME},
ONLINE_TIME_CHANGE= #{ONLINE_TIME_CHANGE}
where USER_ID=#{USER_ID}

update SYS_USER set
OPEN_ID=#{OPEN_ID}
where USERNAME=#{USERNAME}

update SYS_USER set 
OPEN_ID=NULL
where USERNAME=#{USERNAME}

mapper.select:
select USER_ID, USERNAME, NAME, DEPT_ID, DEPT_NAME
from sys_user
where USERNAME = #{username} and DEPT_ID = #{deptId}
