mapper.update:
update SYS_USER set 
SKIN = #{SKIN} 
where USERNAME = #{USERNAME}

update SYS_USER set 
LAST_LOGIN = #{LAST_LOGIN} 
where USER_ID = #{USER_ID}

mapper.select:
	select u.*,(select JOB_RANK 
		from bd_position_level where ID=(
			select EMP_GRADE_ID from bd_employee where EMP_CODE=u.NUMBER )
		) JOB_RANK,
		(select NAME from sys_dictionaries where BIANMA = 
			(select AREA from bd_department where ID= u.DEPT_ID)
		) DEPT_AREA,
		(select AREA from bd_department where ID= u.DEPT_ID) AREA_BIANMA
	from SYS_USER u
	where 1=1

  		and	u.USERNAME = #{USERNAME} and u.PASSWORD = #{PASSWORD}


		and u.USER_ID = #{USER_ID}

select u.*,(select JOB_RANK 
	from bd_position_level where ID=(
		select EMP_GRADE_ID from bd_employee where EMP_CODE=u.NUMBER )
	) JOB_RANK,
	(select NAME from sys_dictionaries where BIANMA = 
		(select AREA from bd_department where ID= u.DEPT_ID)
	) DEPT_AREA,
	(select AREA from bd_department where ID= u.DEPT_ID) AREA_BIANMA
from SYS_USER u
where 
	u.NUMBER = #{NUMBER}

	and u.USERNAME != #{USERNAME}

mapper.update:
update SYS_USER 
	set 
		IP = #{IP}
	where 
		USERNAME = #{USERNAME}

update  SYS_USER
	set USERNAME				= #{USERNAME},
		NAME 					= #{NAME},
		ROLE_ID 				= #{ROLE_ID}

		,DEPT_ID 				= #{DEPT_ID}


		,DEPT_NAME 				= #{DEPT_NAME}

		,BZ						= #{BZ}

		,EMAIL					= #{EMAIL}

		,NUMBER					= #{NUMBER}

		,PHONE					= #{PHONE}


		,PASSWORD				= #{PASSWORD}

	where 
		USER_ID = #{USER_ID}

mapper.select:
select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, u.DEPT_ID, u.DEPT_NAME, u.CREATE_TIME, r.ROLE_ID, r.ROLE_NAME, DATEDIFF(NOW(), u.CREATE_TIME) createdDays from SYS_USER u LEFT JOIN SYS_ROLE r on u.ROLE_ID = r.ROLE_ID where u.USERNAME != 'admin' and u.ENABLED=1 and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and u.ROLE_ID=#{pd.ROLE_ID}
and u.DEPT_ID=#{pd.DEPT_ID}
and DATE_FORMAT(u.LAST_LOGIN, '%Y-%m-%d') >= #{pd.lastLoginStart}
and DATE_FORMAT(u.LAST_LOGIN, '%Y-%m-%d') <= #{pd.lastLoginEnd}
order by ${pd.sortKey} ${pd.sortMethod}
order by u.LAST_LOGIN desc

select u.USER_ID, u.USERNAME, u.PASSWORD, u.NAME, u.DEPT_ID, u.DEPT_NAME, u.LAST_LOGIN, u.IP, u.BZ, u.EMAIL, u.NUMBER, u.PHONE, r.ROLE_ID, r.ROLE_NAME from SYS_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') )
and u.ROLE_ID=#{ROLE_ID}
and u.DEPT_ID=#{DEPT_ID}
and DATE_FORMAT(u.LAST_LOGIN, '%Y-%m-%d') >= #{lastLoginStart}
and DATE_FORMAT(u.LAST_LOGIN, '%Y-%m-%d') <= #{lastLoginEnd}
order by u.LAST_LOGIN desc
